import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { IProducto } from './productos';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent {
  public productos: IProducto[] = [];
  public nuevoProducto: IProducto = { id: 0, nombre: '', descripcion: '', precio: 0, stock: 0 };

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {
    this.cargarProductos();
  }

  cargarProductos() {
    this.http.get<IProducto[]>(this.baseUrl + 'productos').subscribe(result => {
      this.productos = result;
    }, error => console.error(error));
  }

  agregarProducto() {
    this.http.post<IProducto>(this.baseUrl + 'productos', this.nuevoProducto).subscribe(result => {
      this.productos.push(result);
      this.nuevoProducto = { id: 0, nombre: '', descripcion: '', precio: 0, stock: 0 };
    }, error => console.error(error));
  }

  eliminarProducto(id: number) {
    this.http.delete(this.baseUrl + `productos/${id}`).subscribe(result => {
      this.productos = this.productos.filter(p => p.id !== id);
    }, error => console.error(error));
  }

  actualizarProducto(producto: IProducto) {
    this.http.put<IProducto>(this.baseUrl + `productos/${producto.id}`, producto).subscribe(result => {
      const index = this.productos.findIndex(p => p.id === producto.id);
      this.productos[index] = result;
    }, error => console.error(error));
  }
}
